(69, 'woo-activation-survey-blockers-22AUG23', 'info', 'en_US', 'How can we help you get that first sale?', 'Your feedback is vital. Please take a minute to share your experience of setting up your new store and whether anything is preventing you from making those first few sales. Together, we can make Woo even better!', '{}', 'pending', 'woocommerce.com', '2023-09-12 19:20:55', NULL, 0, 'plain', '', 0, 0, 'info'),
(70, 'klaviyo_q3_2023', 'marketing', 'en_US', 'Build long lasting relationships with Klaviyo for WooCommerce', 'Increase customer engagement and boost conversions with powerful email and SMS automation from Klaviyo. Fully integrated with your Woo store, Klaviyo enables you to send personalized communications to your customers — ensuring that the right message is delivered at the right time for maximum impact.', '{}', 'pending', 'woocommerce.com', '2023-09-12 19:20:55', NULL, 0, 'plain', '', 0, 0, 'info');
